package com.test.base;

import com.test.SolutionA;
import com.test.SolutionB;

import junit.framework.TestCase;

public class Example extends TestCase
{
    private Solution solution;
    
    @Override
    protected void setUp()
        throws Exception
    {
        super.setUp();
    }
    
    public void testSolutionA()
    {
        solution = new SolutionA();
        assertSolution();
    }
    
    public void testSolutionB()
    {
        solution = new SolutionB();
        assertSolution();
    }
    
    private void assertSolution()
    {
        String resultA = solution.shortestCommonSupersequence("abac", "cab");
        System.out.println(resultA);
        
        String resultB = solution.shortestCommonSupersequence("bbbaaaba", "bbababbb");
        System.out.println(resultB);
        
        String resultC = solution.shortestCommonSupersequence(
            "bcaaacbbbcbdcaddadcacbdddcdcccdadadcbabaccbccdcdcbcaccacbbdcbabb",
            "dddbbdcbccaccbababaacbcbacdddcdabadcacddbacadabdabcdbaaabaccbdaa");
        System.out.println(resultC);
        /* 这一段，异常了。所以，递归太长；改用动态规划
        String resultD = solution.shortestCommonSupersequence(
            "",
            "");
        System.out.println(resultD);*/
    }
    
    @Override
    protected void tearDown()
        throws Exception
    {
        super.tearDown();
    }
}
